INSERT INTO movie (id, title, year, genre, plot) VALUES (1, 'The Shining', 1980, 'Drama', 'Jack and his family move into an isolated hotel with a violent past. Living in isolation, Jack begins to lose his sanity, which affects his family members.');
INSERT INTO movie (id, title, year, genre, plot) VALUES (2, 'Leon', 1994, 'Drama', 'When 12-year-old Mathilda''s family is killed, her neighbour Leon, who is a professional assassin, reluctantly takes her under his wing and teaches her the secrets of his trade.');
INSERT INTO movie (id, title, year, genre, plot) VALUES (3, 'The Wolf of Wall Street', 2014, 'Drama', 'Introduced to life in the fast lane through stockbroking, Jordan Belfort takes a hit after a Wall Street crash. He teams up with Donnie Azoff, cheating his way to the top as his relationships slide.');
